import "./preloader.css";

export default function Preloader() {
  const [filled, setFilled] = useState(0);
  const [isRunning, setIsRunning] = useState(true);
  useEffect(() => {
    if (filled < 100 && isRunning) {
      setTimeout(() => setFilled((prev) => (prev += 2)), 20);
    }
    if (filled == 100) {
      setIsRunning(false);
    }
