    }
  }, [filled, isRunning]);

  return (
    <div>
      <div>
        <div id="preloader">
          <div id="percent">{filled}%</div>

          <div id="bar">
            <div
              id="barconfrm"
              style={{
                width: `${filled}%`,
                transition: "width 0.2s",
              }}
            ></div>
          </div>
        </div>

        <div class="container">
          <h1>HELLOOOt</h1>
        </div>
      </div>
    </div>
  );
}
